import React, { Component } from 'react';
import { tsParticles } from 'tsparticles';

class ParticleSettings extends Component {
  render() {
    return (
      <div id='content' className='mt-3'>
        <Particle 
          height='100vw' width='100vw'
          id='tsparticles'
          options={{
            background: {
              color: {
                value: "#0d47a1"
              }
            },
            fpsLimit: 60,
            interactivity: {
              detect_on: 'canvas',
              events: {
                onClick: {
                  enable: true,
                  mode: 'push'
                },
                onHover: {
                  enable: true,
                  mode: 'repulse'
                },
                resize: true
              },
              modes: {
                bubble: {
                  distance: 400,
                  duration: 2,
                  opacity: 0.8,
                  size: 40
                },
                push: {
                  quantity: 4
                },
                repulse: {
                  distance: 200,
                  duration: 0.4
                }
              }
            },
            particles: {
              color: {
                value: '#ffffff'
              },
              links: {
                color: '#ffffff',
                distance: 150,
                enable: true,
                opacity: 0.5,
                width: 1
              },
              collisions: {
                enable: true
              },
              move: {
                enable: true,
                speed: 1
              },
              number: {
                density: {
                  enable: true,
                  area: 800
                },
                value: 80
              },
              opacity: {
                value: 0.5
              },
              shape: {
                type: 'circle'
              },
              size: {
                random: true,
                value: 5
              }
            },
            detectRetina: true
          }}
        />
      </div>
    );
  }
}

export default ParticleSettings;
