import React, {Component} from 'react'
import { green } from 'ansi-colors'
import './App.css'
import Navbar from './Nav.js'
import Web3 from 'web3'
import Tether from '../truffle_abis/Tether.json'

class App extends Component{
    //calls once we load the page
    async UNSAFE_componentWillMount(){
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    //Connect to web3 provider
    async loadWeb3() {
        if(window.ethereuem){
            window.web3 = new Web3(window.ethereum)
            await window.ethereuem.enable()
        }else if(window.web3){
            window.web3 = new Web3(window.web3.currentProvider)
        }else{
            alert("Mummu")
        }
    }

    //convert eth
    tokenToWei (number){
        //return web3.utils.toWei (number,'Ether');
        return Web3.utils.toWei(number, 'Ether');
    }

    tokenFromWei(number){
        return Web3.utils.fromWei(number);
    }

    //this gets the data from the blockchain
    async loadBlockchainData(){
        const web3 = window.web3
        const account = await web3.eth.getAccounts()
        this.setState({account: account[0]})

        //get the network id of network user is connected to
        const networkId = await web3.eth.net.getId()

        //Load the tether contract
        const tetherData = Tether.networks[networkId]
        if(tetherData){
            const tether = new web3.eth.Contract(Tether.abi, tetherData.address);
            this.setState({tether})
            let tetherBalance = await tether.methods.balanceOf(this.state.account).call()
            tokenFromWei(this.setState({tetherBalance: tetherBalance.toString() }))
            console.log(tetherBalance)
        }else{
            window.alert('Tether contract not depoyed')
        }
    }

    constructor(props){
        super(props)
        this.state = {
            account: '0x2',
            tether: {},
            rwd : {},
            decentraBank: {},
            tetherBalance: '0',
            rwdBalance: '0',
            stakingBalance: '0',
            loading: true
        }
    }

    render(){
        return(
            <div>
                <Navbar account={this.state.account}/>
                <div className="text-center" >
                </div>
            </div>
        )
    }
}

export default App;
