import React, {Component} from 'react'
import Web3 from 'web3'
import tether from '../tether.png'

class Main extends Component{
    tokenToWei (number){
        if (number !== undefined && number !== null) {
            return Web3.utils.toWei(number.toString(), 'Ether');
        }
        return '0';
    }

    tokenFromWei(number){
        if (number !== undefined && number !== null) {
        return Web3.utils.fromWei(number.toString());
        }
        return '0';
    }
    render(){
        return(
            <div id='content' className='mt-3'>
                <table className='table text-muted text-center'>
                    <thead>
                        <tr style={{color:'black'}}>
                            <th scope='col'>
                                Staking Balance
                            </th>
                            <th scope='col'>
                                Reward Balance
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr style={{color:'black'}}>
                            <td>
                                USDT:  {this.tokenFromWei(this.props.tetherBalance)}
                            </td>
                            <td>
                                RWD: {this.tokenFromWei(this.props.rwdBalance)}
                            </td>
                        </tr>
                    </tbody>
                </table>
                <div className='card mb-2' style={{opacity:'.9'}}>
                    <form className='mb-3'>
                        <diiv style={{borderSpace:'0 1em'}}>
                            <label className='float-left' style={{marginLeft:'15px'}}>
                                <b>
                                    Stake token
                                </b>
                            </label>
                            <span className='float-right' style={{marginRight:'8px'}}>
                                Balance:
                            </span>
                            <div className='input-group mb-4'>
                                <input type='text' placeholder= '0' required />
                                <div className='input-grouped-open'>
                                    <div className='input-group-text'>
                                        <img alt= 'tether' scr={tether} />
                                    </div>
                                </div>
                            </div>
                        </diiv>

                    </form>
                </div>
            </div>
        )
    }
}

export default Main;
