import React, {Component} from 'react'
import Particle from 'react-tsparticles'

class ParticleSettings extends Component{
    render(){
        return(
            <div id='content' className='mt-3'>
                <Particle 
                height='100vw' width='100vw'
                id ='tsparticles'
                options={
                    {
                        background:{
                            color:{
                                value:"#0d47a1"
                            },
                        },
                        fpslimit:60,
                        interactivity:{
                            detect_on:'canvas',
                            events:{
                                onClick:{
                                    enable: 'true',
                                    mode:'push'
                                },
                                onHover:{
                                    enable:'true',
                                    mode:'repulse'
                                },
                                resize:'true',
                            },
                            modes:{
                                bubble:{
                                    distance:400,
                                    duration: 2,
                                    opacity: 0.8,
                                    size: 40
                                }
                            }
                        }
                    }
                }
                />
            </div>
        )
    }
}

export default ParticleSettings;
